clear all;close all;fig=1;available_graphics_toolkits()loaded_graphics_toolkits()graphics_toolkit ("qt");fsampl = 25e6; #[Hz]t_end = 0.001; #[s]t= linspace(0,t_end,t_end.*fsampl);fT = 10e6; #[Hz]phase = (3.*pi)./4; #[rad] - 135Â°x1 = 2.*pi.*t.*fT+phase;fS = 3e6; #[Hz]x2 = 2.*pi.*t.*fS;am_signal = sin(x1).*sin(x2);figure(fig++);plot(t,am_signal);grid on;Fs=20.*log10(abs(fft(am_signal)));figure(fig++);freqencies=linspace(0,fsampl./2,length(Fs)./2);plot(freqencies,Fs(:,1:1:(length(Fs)./2)));grid on;